script.value:
const fileName = $actions.getVar("fileName");
const inputText = $actions.getVar("inputText");

const formats = [
  { name: "Microsoft Word", ext: "docx" },
  { name: "HTML", ext: "html" },
  { name: "PDF", ext: "pdf" },
  { name: "Rich Text Format", ext: "rtf" },
  { name: "EPUB Book", ext: "epub" },
  { name: "Kindle Book", ext: "mobi" },
  { name: "LaTex", ext: "latex" },
  { name: "AsciiDoc", ext: "asciidoc" }
];

const keys = {
  format: "format",
  smart: "smart",
  noWrap: "noWrap",
  toc: "toc",
  options: "options"
};

const states = {
  format: $cache.get(keys.format) || "docx",
  smart: $cache.get(keys.smart) || false,
  noWrap: $cache.get(keys.noWrap) || false,
  toc: $cache.get(keys.toc) || false,
  options: $cache.get(keys.options) || "",
  loading: false
};

const template = [
  {
    type: "label",
    props: {
      id: "title"
    },
    layout: (make, view) => {
      make.left.right.inset(15);
      make.centerY.equalTo(view.super);
    }
  },
  {
    type: "label",
    props: {
      id: "detail"
    },
    layout: (make, view) => {
      make.right.inset(15);
      make.centerY.equalTo(view.super);
    }
  },
  {
    type: "switch",
    props: {
      id: "switcher"
    },
    layout: (make, view) => {
      make.right.inset(15);
      make.centerY.equalTo(view.super);
    },
    events: {
      changed: (sender) => {
        states[sender.info] = sender.on;
        $cache.set(sender.info, sender.on);
      }
    }
  },
  {
    type: "spinner",
    props: {
      id: "spinner"
    },
    layout: $layout.center
  }
];

const handlers = {
  selectFormat,
  options,
  convert
};

$ui.render({
  props: {
    title: "Docverter"
  },
  views: [
    {
      type: "list",
      props: {
        style: 2,
        template: template
      },
      layout: $layout.fill,
      events: {
        didSelect: ($0, $1, { handler }) => {
          if (handlers[handler]) {
            handlers[handler]();
          }
        }
      }
    }
  ]
});

function reloadData() {
  const list = $("list");
  list.data = [
    {
      rows: [
        {
          title: {
            text: "Format"
          },
          detail: {
            text: (() => {
              for (const format of formats) {
                if (format.ext === states.format) {
                  return format.name;
                }
              }
              return "";
            })()
          },
          handler: "selectFormat",
          switcher: { hidden: true },
          spinner: { loading: false }
        }
      ]
    },
    {
      rows: [
        {
          title: {
            text: "Smart Quotes"
          },
          switcher: {
            info: keys.smart,
            on: states.smart
          },
          detail: { hidden: true },
          spinner: { loading: false }
        },
        {
          title: {
            text: "No Line Wrapping"
          },
          switcher: {
            info: keys.noWrap,
            on: states.noWrap
          },
          detail: { hidden: true },
          spinner: { loading: false }
        },
        {
          title: {
            text: "Table of Contents"
          },
          switcher: {
            info: keys.toc,
            on: states.toc
          },
          detail: { hidden: true },
          spinner: { loading: false }
        },
        {
          title: {
            text: "More Options..."
          },
          handler: "options",
          detail: { hidden: true },
          switcher: { hidden: true },
          spinner: { loading: false }
        }
      ]
    },
    {
      rows: [
        {
          title: {
            text: "Convert",
            textColor: $color("tint"),
            align: $align.center,
            hidden: states.loading
          },
          spinner: {
            loading: states.loading
          },
          handler: "convert",
          detail: { hidden: true },
          switcher: { hidden: true }
        }
      ]
    }
  ];
}

reloadData();

async function selectFormat() {
  const { index } = await $ui.menu(formats.map((x) => `${x.name} (${x.ext})`));
  states.format = formats[index].ext;

  $cache.set(keys.format, states.format);
  reloadData();
}

function options() {
  $ui.push({
    props: {
      title: "More Options",
      navButtons: [
        {
          title: "Docs",
          handler: () => {
            $app.openURL("https://www.docverter.com/api/");
          }
        }
      ]
    },
    views: [
      {
        type: "text",
        props: {
          type: $kbType.ascii,
          text: states.options,
          font: $font("Menlo-Regular", 15),
          placeholder: "E.g., --base_header_level=2 --strict"
        },
        layout: $layout.fill,
        events: {
          changed: (sender) => {
            states.options = sender.text;
            $cache.set(keys.options, sender.text);
          }
        }
      }
    ]
  });
}

async function convert() {
  if (states.loading) {
    return;
  }

  states.loading = true;
  reloadData();

  const form = {
    from: "markdown",
    to: states.format
  };

  const enableFlag = (flag) => {
    form[flag] = "true";
  };

  if (states.smart) {
    enableFlag("smart");
  }

  if (states.noWrap) {
    enableFlag("no_wrap");
  }

  if (states.toc) {
    enableFlag("table_of_contents");
  }

  states.options.split("--").forEach((option) => {
    if (option.length == 0) {
      return;
    }

    const pairs = option.split("=");
    if (pairs.length == 2) {
      form[pairs[0].trim()] = pairs[1].trim();
    } else {
      enableFlag(option.trim());
    }
  });

  const result = await $http.upload({
    url: "http://c.docverter.com/convert",
    form,
    files: [
      {
        name: "input_files[]",
        filename: "file.md",
        data: $data({ string: inputText })
      }
    ]
  });

  states.loading = false;
  reloadData();
  $actions.finish();

  if (result.response.statusCode != 200) {
    $ui.alert(`Error: ${result.response.statusCode}, ${result.data.error}`);
    return;
  }

  let ext = states.format;
  if (ext === "latex") {
    ext = "tex";
  } else if (ext === "asciidoc") {
    ext = "txt";
  }

  $share.sheet({
    name: `${fileName}.${ext}`,
    data: result.rawData
  });
}
